```php
public static function factory(
        string $type,
        int $id,
        string $firstname,
        string $surname,
        string $email
    ): Customer {
 $classname = __NAMESPACE__ . '\\' . ucfirst($type);
 if (!class_exists($classname)) {
 throw new \InvalidArgumentException('Wrong type.');
 }
 return new $classname($id, $firstname, $surname, $email);
}
