Route::get('cats', function() {
  $cats = Furbook\Cat::all();
  return view('cats.index')->with('cats', $cats);
});

Route::get('cats/breeds/{name}', function($name) {
 $breed = Furbook\Breed::with('cats')
 ->whereName($name)
 ->first();
  return view('cats.index')
    ->with('breed', $breed)
    ->with('cats', $breed->cats);
});
