            throw new Exception("Insufficient Data provided");
        }
    } catch (Mage_Core_Exception $e) {
        Mage::getSingleton('core/session')->addError($e- >getMessage());
        $this->_redirect('*/*/');
    }
    $this->_redirect('*/*/');
}
