    public function prepareStatusLayout($value)
    {
      $class = '';
      switch ($value) {
        case 'pending' :
          $class = 'grid-severity-notice';
          break;
        case 'approved' :
          $class = 'grid-severity-major';
          break;
        case 'declined' :
          $class = 'grid-severity-critical';
          break;
      }
      return '<span class="'.$class.'"><span>'.$value.'</span></span>';
    }
