/**
* Handle HTTP basic auth
* @param string $email
* @param string $password
* @return static self
*/
public function httpBasicAuth($email, $password)
{
    $model = static::findOne(['email' => $email]);
    if ($model == NULL)
        return NULL;

    if (password_verify($password, $model->password))
        return $model;

    return NULL;
}
