function wpquick_restrict_content_display( $atts, $content ){
  $sh_attr = shortcode_atts( array( 'role' => '' ), $atts );

  if( $sh_attr['role'] != '' && ! current_user_can( $sh_attr['role'] ) ){
    $content = __('You don\'t have permission to view this content','wqsa');
  } 
  return $content;
}
