function ch2pit_private_shortcode( $atts, $content = null ) { 
    if ( is_user_logged_in() ) {
        return '<div class="private">' . $content . '</div>'; 
    } else {
        $output = '<div class="register">';
        $output .= 'You need to become a member to access ';
        $output .= 'this content.</div>';
        return $output;
    }
} 
