function ch11hw_set_default_options_array() { 
    if ( false === get_option( 'ch11hw_options' ) ) { 
        $new_options = array(); 
        $new_options['default_text'] = __( 'Hello World',
                                           'ch11hw_hello_world' ); 
        add_option( 'ch11hw_options', $new_options ); 
    }  
} 
