function build_taxonomies() { 
  register_taxonomy( 
    'book_category', 
    'book', 
    array( 
      'hierarchical' => true, 
      'public' => true, 
      'show_in_rest' => true, 
      'label' => 'Book Category', 
      'query_var' => true, 
      'rewrite' => array('slug' => 'available-books') 
    ) 
  ); 
} 
add_action('init', 'build_taxonomies', 0);
