            public function actionSendCombine()
            {
                $name = 'John';

                Yii::$app->mailer->compose(['html' => 
                'message-html', 'text' => 'message-text'], [
                'name' => $name,
                ])
                ->setTo('to@yii-book.app')
                ->setFrom(['from@yii-book.app' 
                    => Yii::$app->name])
                ->setSubject('My Test Message')
                ->send();
            }
    }
